mapper.update:
UPDATE member
SET
mem_name = #{memberName},
mem_phone = #{memberPhone},
mem_addr = #{memberAddr},
mem_detail_addr = #{memberDetailAddr},
mem_acc = #{memberAcc}
mem_auth = #{memberAuth}
WHERE mem_no = #{memberNo}

mapper.select:
SELECT bid_no, art_no, mem_no, bid_price, bid_datetime
FROM bid
WHERE mem_no = #{memberNo}

